 int left = 0; 
 int right = s.length() - 1; 
 while (left < right) { 
 char temp = charArray[left]; 
 charArray[left] = charArray[right]; 
 charArray[right] = temp; 
 left++; 
 right--; 
 } 
 return new String(charArray); 
 } 

